script:
# 自动在取消提交时移除 User Permission
# doc.name 是 Patent Writer 记录的 name（即 writer_id，如 "WRT-John_Doe-001"）

user = frappe.db.get_value('User',
    {'email': doc.email}, 'name')

if user:
    up_name = frappe.db.get_value('User Permission', {
        'user': user, 'allow': 'Patent Writer', 'for_value': doc.name
    }, 'name')

    if up_name:
        frappe.delete_doc('User Permission', up_name, force=True)
        frappe.msgprint(f"🛑 已移除用户 {user} 对 Patent Writer '{doc.name}' 的访问权限", alert=True)
